import '../../App.css'
import {React, setState, useState} from 'react'
import CardItem from '../CardItem'
import {Link} from 'react-router-dom';
import { Button } from '../Button';
import backgroundVideo from '../../videos/video-3.mp4'
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';

import {OutlinedInput,
        InputAdornment,
        InputLabel,
        IconButton,
        FormControl,
        TextField,
        Paper, 
        FormGroup } from '@material-ui/core';

export default function SignUpFormCreator(){
    const [password, setPassword] = useState("");
    const [email, setEmail] = useState("");
    const [showPassword, setShowPassword] = useState(false);

    function handleChange(value, event){
        if(value === "email")
            setEmail(event.target.value);
        else if(value === "password"){
            setPassword(event.target.value);
        }
    }
    const handleClickShowPassword =()=> setShowPassword(!showPassword);

    const handleMouseDownPassword = (event) => {
        event.preventDefault();
      };

    return (
        <Paper>
            <form>
                <FormControl className=''>
                    <FormGroup>
                        {/* <InputLabel htmlFor="email">Email address</InputLabel> */}
                        <TextField id="name" label="Name" variant="filled" />
                    </FormGroup>
                    <FormGroup>
                        {/* <InputLabel htmlFor="email">Email address</InputLabel> */}
                        <TextField id="surname" label="Surname" variant="filled" />
                    </FormGroup>
            <FormControl required fullWidth margin="normal">
              <InputLabel htmlFor="email" className={classes.labels}>
                e-mail
              </InputLabel>
              <Input
                name="email"
                type="email"
                autoComplete="email"
                className={classes.inputs}
                disableUnderline={true}
                onChange={this.handleChange("email")}
              />
            </FormControl>
                    <FormGroup>
                    <FormControl sx={{ m: 1, width: '25ch' }} variant="outlined">
                        <InputLabel htmlFor="outlined-adornment-password">Password</InputLabel>
                        <OutlinedInput
                            id="password"
                            type={showPassword ? 'text' : 'password'}
                            value={password}
                            onChange={(e) => handleChange('password', e)}
                            endAdornment={
                            <InputAdornment position="end">
                                <IconButton
                                    aria-label="toggle password visibility"
                                    onClick={handleClickShowPassword}
                                    onMouseDown={handleMouseDownPassword}
                                    edge="end"
                                >
                                {showPassword ? <VisibilityOff /> : <Visibility />}
                                </IconButton>
                            </InputAdornment>
                            }
                            label="Password"
                        />
                        </FormControl>
                    </FormGroup>
                    <FormControl sx={{ m: 1, width: '25ch' }} variant="outlined">
                        <InputLabel htmlFor="outlined-adornment-password">Confirm Password</InputLabel>
                        <OutlinedInput
                            id="confirm password"
                            type={showPassword ? 'text' : 'password'}
                            value={password}
                            onChange={(e) => handleChange('password', e)}
                            endAdornment={
                            <InputAdornment position="end">
                                <IconButton
                                    aria-label="toggle password visibility"
                                    onClick={handleClickShowPassword}
                                    onMouseDown={handleMouseDownPassword}
                                    edge="end"
                                >
                                {showPassword ? <VisibilityOff /> : <Visibility />}
                                </IconButton>
                            </InputAdornment>
                            }
                            label="Confirm Password"
                        />
                        </FormControl>
                </FormControl>
            </form>
        </Paper>
    );
}
